package com.example.navigationimport android.util.Logimport androidx.compose.runtime.Composableimport androidx.navigation.NavControllerimport androidx.navigation.NavTypeimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport androidx.navigation.navArgumentimport com.example.navigation.ui.screen.EcranDetailsJeuimport com.example.navigation.ui.screen.EcranListeJeux@Composablefun MonApplication() {    val navController = rememberNavController()    NavHost(navController = navController, startDestination = "ListeJeux" ) {        composable("ListeJeux"){            EcranListeJeux(navController)        }        composable("DetailsJeu/{jeuId}", arguments = listOf(navArgument("jeuId") { type = NavType.IntType})) {backStackEntry ->            val jeuId = backStackEntry.arguments?.getInt("jeuId") ?: 0            Log.d("Navigation", "Navigu√© vers DetailsJeu avec ID: $jeuId")            EcranDetailsJeu(jeuId = jeuId)        }    }}